namespace TTLX.WindowsTool.Common.Utility
{
    using System;
    using System.Text;

    public class PinyinHelper
    {
        private static string pinyinIndex = "";

        public static string GetPinyin(string strText)
        {
            try
            {
                if (string.IsNullOrEmpty(strText))
                {
                    return strText;
                }
                StringBuilder builder = new StringBuilder();
                foreach (char ch in strText)
                {
                    if ((ch >= '一') && (ch <= 0x9fa5))
                    {
                        builder.Append(pinyinIndex[ch - '一']);
                    }
                    else
                    {
                        builder.Append(char.ToUpper(ch));
                    }
                }
                return builder.ToString();
            }
            catch (Exception exception1)
            {
                string message = exception1.Message;
                return "";
            }
        }
    }
}

